package io.xdag.crypto;

import io.xdag.config.Config;
import io.xdag.crypto.jni.Native;
import org.apache.commons.io.IOUtils;
import org.junit.Before;
import org.junit.Test;

import java.io.File;
import java.io.FileInputStream;

import static org.junit.Assert.assertTrue;

public class DnetKeysTest {

  public byte[] dnetKeys;
  public Config config;

  public final String cWord =
      "";
  public final String cPwd = "08d599aafccb4e64a97d31cc2e8204ac";

  @Before
  public void setUp() throws Exception {
    config = new Config();
    config.initKeys();
    //		Resource resource = new ClassPathResource("dnet_keys.bin");
    //		File keyFile =  resource.getFile();
    String absolutePath = Native.class.getClassLoader().getResource("dnet_keys.bin").getPath();
    File keyFile = new File(absolutePath);

    dnetKeys = new byte[3072];

    IOUtils.read(new FileInputStream(keyFile), dnetKeys);

    byte[] prvKey = new byte[1024];
    byte[] pubKey = new byte[1024];
    byte[] encodedWord = new byte[512];
    byte[] word = new byte[512];

    System.arraycopy(dnetKeys, 0, prvKey, 0, 1024);
    System.arraycopy(dnetKeys, 1024, pubKey, 0, 1024);
    System.arraycopy(dnetKeys, 2048, encodedWord, 0, 512);
    System.arraycopy(dnetKeys, 2560, word, 0, 512);
  }

  @Test
  public void testInitKeys() throws Exception {

    for (int i = 0; i < 3072; i++) {
      if (i < 1024) {
        assertTrue(dnetKeys[i] == config.getXKeys().prv[i]);
      } else if (i < 2048) {
        assertTrue(dnetKeys[i] == config.getXKeys().pub[i - 1024]);
      } else if (i < 2560) {
        assertTrue(dnetKeys[i] == config.getXKeys().sect0_encoded[i - 2048]);
      } else if (i < 3072) {
        assertTrue(dnetKeys[i] == config.getXKeys().sect0[i - 2560]);
      }
    }
  }
}
